import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // For navigation
import UserService from '../services/UserService';


const SignInPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate(); // Hook for navigation

  const handleSubmit = async (e) => {
    e.preventDefault();

    const credentials = { email, password };
    try {
      const response = await UserService.loginUser(credentials);
      console.log('Login successful', response);
      alert('Invalid email or password');
      navigate('/homepage');
    } catch (error) {
      alert('Invalid email or password');
      console.error('Login failed', error);
    }
  };

  return (
    <div className="flex flex-col min-h-screen bg-gray-100">
      <header className="bg-white py-4 px-6 flex justify-between items-center">
        <h1 className="text-xl font-bold">Property Management</h1>
      </header>
      <main className="flex-grow flex justify-center items-center px-6">
        <div className="max-w-md space-y-4">
          <h2 className="text-2xl font-bold">Sign In</h2>
          <form onSubmit={handleSubmit}>
            <div className="mb-4">
              <label htmlFor="email" className="block text-gray-700 font-bold mb-2">
                Email/Username
              </label>
              <input
                type="text"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="w-full px-3 py-2 rounded-md border border-gray-300 focus:outline-none focus:ring-1 focus:ring-blue-500"
                required
              />
            </div>
            <div className="mb-6">
              <label htmlFor="password" className="block text-gray-700 font-bold mb-2">
                Password
              </label>
              <input
                type="password"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                className="w-full px-3 py-2 rounded-md border border-gray-300 focus:outline-none focus:ring-1 focus:ring-blue-500"
                required
              />
            </div>
            <button
              type="submit"
              className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-700 focus:outline-none"
            >
              Sign In
            </button>
          </form>
        </div>
      </main>
      <footer className="bg-gray-200 py-4 px-6 text-center text-gray-500">
        &copy; 2024 Property Management App
      </footer>
    </div>
  );
};

export default SignInPage;
