import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const HomePage = () => {
    const navigate = useNavigate();
    const [showProfileMenu, setShowProfileMenu] = useState(false);

    const handleSignOut = () => {
        // Implement logic to handle sign out
        navigate('/signin'); // Redirect to signin page on signout
    };

    const toggleProfileMenu = () => {
        setShowProfileMenu(!showProfileMenu);
    };

    return (
        <div className="flex flex-col min-h-screen bg-gray-100">
            {/* Header with navigation and profile dropdown */}
            <header className="bg-white py-4 px-6 flex justify-between items-center shadow">
                {/* Left section with logo and navigation links */}
                <div className="flex items-center space-x-4">
                    {/* Logo */}
                    <div className="h-8 w-8 bg-gray-200 rounded-full"></div>
                    {/* Navigation Links */}
                    <nav className="flex space-x-4">
                        <a href="#" className="text-blue-600 hover:underline">Dashboard</a>
                        {/* Microservices Dropdown or Links */}
                        <div className="relative">
                            <button className="text-blue-600 hover:underline focus:outline-none" onClick={() => navigate('/properties')}>
                                Properties
                            </button>
                            <button className="text-blue-600 hover:underline focus:outline-none" onClick={() => navigate('/maintenance-requests')}>
                                Maintenance Requests
                            </button>
                            <button className="text-blue-600 hover:underline focus:outline-none" onClick={() => navigate('/notifications')}>
                                Notifications
                            </button>
                            <button className="text-blue-600 hover:underline focus:outline-none" onClick={() => navigate('/payments')}>
                                Payments
                            </button>
                            {/* Add more microservices links/buttons as needed */}
                        </div>
                    </nav>
                </div>

                {/* Profile Dropdown */}
                <div className="relative">
                    <button onClick={toggleProfileMenu} className="flex items-center space-x-2 focus:outline-none">
                        <span className="text-gray-600">Welcome, [User's Full Name]!</span> {/* Replace with actual user's name */}
                        <svg xmlns="http://www.w3.org/2000/svg" className="h-6 w-6 text-gray-600" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M19 9l-7 7-7-7" />
                        </svg>
                    </button>
                    {showProfileMenu && (
                        <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg py-2 z-10">
                            <button className="block w-full text-left px-4 py-2 text-sm text-gray-600 hover:bg-gray-100" onClick={handleSignOut}>
                                Sign Out
                            </button>
                            <button className="block w-full text-left px-4 py-2 text-sm text-gray-600 hover:bg-gray-100">
                                Profile
                            </button>
                            {/* Add more profile options as needed */}
                        </div>
                    )}
                </div>
            </header>

            {/* Main Content */}
            <main className="flex-grow px-6 py-8">
                {/* Main content will dynamically load based on routing */}
            </main>

            {/* Footer */}
            <footer className="bg-gray-200 py-4 px-6 text-center text-gray-500">
                <div className="flex justify-center space-x-4">
                    <a href="/contact" className="hover:underline">Contact Info</a>
                    <a href="/privacy-policy" className="hover:underline">Privacy Policy</a>
                    <a href="/terms-of-service" className="hover:underline">Terms of Service</a>
                    <a href="/help-center" className="hover:underline">Help Center</a>
                </div>
                <div className="mt-4">
                    &copy; 2024 Property Management App
                </div>
            </footer>
        </div>
    );
};

export default HomePage;
